class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mp;
        int len = nums.size();
        int i=0,j=len-1;
        vector<int> dif;
        while(i<=j){
            mp[abs(nums[i]-nums[j])].push_back(max({nums[i],nums[j],k-nums[i],k-nums[j]}));
            dif.push_back(max({nums[i],nums[j],k-nums[i],k-nums[j]}));
            i++;
            j--;
        }
        int cnt = len+1;
        sort(begin(dif),end(dif));
        for(auto i : mp){
            int lb = lower_bound(begin(dif),end(dif),i.first)-begin(dif);
            int val = len/2+lb;
            for(auto j : i.second){
                if(j < i.first) val-=2;
                else val-=1;
            }
            cnt = min(cnt,val);
        }
        return cnt;
    }
};
