int changes(vector<int> &nums, int k, int x) {
    int n = nums.size(), c = 0;
    for (int i = 0; i < nums.size() / 2; ++i) {
        int lo = nums[i], hi = nums[n - i - 1];
        if (lo > hi)
            swap(lo, hi);
        if (hi - lo == x)
            continue;
        if (lo + x <= k || hi - x >= 0)
            ++c;
        else
            c += 2;
    }
    return c;
}

class Solution {
  public:
    int minChanges(vector<int> &nums, int k) {
        int n = nums.size();
        unordered_map<int, int> diff_cnt;
        for (int i = 0; i < nums.size() / 2; ++i) {
            ++diff_cnt[abs(nums[i] - nums[n - i - 1])];
        }
        vector<int> cnt_minx(n, INT_MAX);
        for (auto [diff, cnt] : diff_cnt) {
            cnt_minx[cnt] = min(cnt_minx[cnt], diff);
        }
        int c = n / 2;
        for (auto minx : cnt_minx) {
            if (minx < INT_MAX) {
                c = min(c, changes(nums, k, minx));
            }
        }
        return c;
    }
};
