class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int ans = numeric_limits<int>::max();
        vector<int> freq(k + 1, 0);
        int n = nums.size();

        vector<int> right;
        for (int i = 0; i < n / 2; i++) {
            freq[abs(nums[i] - nums[n - i - 1])]++;
            right.push_back(max(k - min(nums[i], nums[n - i - 1]), max(nums[i], nums[n - i - 1])));
        }
        // for (auto x : right) cout << x << " "; cout << endl;
        sort(right.begin(), right.end());
        size_t idx = 0;

        for (int i = 0; i <= k; i++) {
            while (idx < right.size() && right[idx] < i) {
                idx++;
            }
            int res = idx * 2 + right.size() - idx - freq[i];
            ans = min(ans, res);
            // cout << "i = " << i << " res = " << res << endl;
        }
        return ans;
    }
};
