class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int>mp;
        int n=nums.size();
        int maxx=INT_MIN,p=-1,ans=0;
        for(int i=0;i<n/2;i++){
            int x = abs(nums[i]-nums[n-i-1]);
            mp[x]++;
            if(mp[x]>=maxx){
                if(mp[x]==maxx){
                    p=min(p,x);
                }
                else{
                    p=x;
                    maxx=mp[x];
                }
            }
        }
        //cout<<p<<endl;
        for(int i=0;i<n/2;i++){
            int x=abs(nums[i]-nums[n-i-1]);
           // cout<<x<<" "<<ans<<endl;
            if(x==p)continue;
            int y=nums[i]+p;
            if(y<=k){
                ans++;
                continue;
            }
            y=nums[i]-p;
             if(y>=0){
                ans++;
                continue;
            }
             y=nums[n-i-1]+p;
            if(y<=k){
                ans++;
                continue;
            }
            y=nums[n-i-1]-p;
             if(y>=0){
                ans++;
                continue;
            }
            ans+=2;
        }
        int maxx2=INT_MIN,p2=-1;
        for(auto it:mp){
            if(it.second!=maxx && it.second > maxx2){
                maxx2=it.second;
                p2=it.first;
            }
        }
        //cout<<maxx2<<" "<<p2;
        if(p2==-1)return ans;
        int ans2=0;
        for(int i=0;i<n/2;i++){
            int x=abs(nums[i]-nums[n-i-1]);
           // cout<<x<<" "<<ans<<endl;
            if(x==p2)continue;
            int y=nums[i]+p2;
            if(y<=k){
                ans2++;
                continue;
            }
            y=nums[i]-p2;
             if(y>=0){
                ans2++;
                continue;
            }
             y=nums[n-i-1]+p2;
            if(y<=k){
                ans2++;
                continue;
            }
            y=nums[n-i-1]-p2;
             if(y>=0){
                ans2++;
                continue;
            }
            ans2+=2;
        }

        return min(ans,ans2);

    }
};
