class Solution {
public:
    int minChanges(vector<int>& A, int k) {
        int n = A.size(), res = n / 2;
        vector<int> ds(k + 1, 0);
        unordered_set<int> bad;
        unordered_map<int, vector<int>> turn;
        for (int i = 0; i < n / 2; ++i) {
            auto j = n - i - 1;
            auto a = A[i], b = A[j];
            if (a > b) swap(A[i], A[j]);
            ++ds[abs(a - b)];
            turn[max({A[i], k - A[i], A[j], k - A[j]})].push_back(i);
        }
        // dbg(ds);
        // dbg(turn);
        for (int x = 0; x <= k; ++x) {
            // dbg(x);
            int m = bad.size();
            // dbg(bad, m);
            res = min(res, 2 * m + (n / 2 - m - ds[x]));
            for (auto idx : turn[x]) bad.insert(idx);
            // dbg(bad);
        }
        return res;
    }
};
