class Solution {
public:

    int helper(vector<int>& temp, int target, unordered_map<int,int>& absMap) {

        int low = 0, high = temp.size()-1, ans = 0;

        while(low <= high) {

            int mid = low + (high-low)/2;

            if(temp[mid] >= target)
                high = mid - 1;
            else {
                ans = mid + 1;
                low = mid + 1;
            }
        }

        return ans * 2 + (temp.size() - ans - absMap[target]);
    }

    int minChanges(vector<int>& nums, int k) {

        int n = nums.size();

        vector<int> temp(n/2);
        unordered_map<int,int> absMap;

        for(int i=0;i<n/2;i++)  {

            int mn = min(nums[i], nums[n-i-1]);
            int mx = max(nums[i], nums[n-i-1]);

            temp[i] = max(mx, k-mn);
            absMap[mx-mn]++;
        }

        sort(temp.begin(), temp.end());

        int ans = INT_MAX;

        for(int i=0;i<=k;i++) {
            ans = min(ans, helper(temp, i, absMap));
        }

        return ans;
    }
};
