class Solution {
public:
    bool inside(int a, int b, int c) {
        return (a <= b) && (b <= c);
    }
    int solve(const vector<int>& a, int d, int k) {
        int result = 0;
        int n = a.size();
        for (int i = 0; i < n / 2; ++i) {
            int r = a[i]; 
            int l = a[n - 1 - i];
            int mn = min(r, l);
            int mx = max(r, l);

            if (mx - mn != d) {
                bool options = inside(0, mn + d, k) | inside(0, mx - d, k);
                if (options) {
                    result += 1;
                } else {
                    result += 2;
                }
            }
        }
        return result;

    }
    int minChanges(vector<int>& a, int k) {
        int n = a.size();
        map<int, int> mp;
        int dd_min = k;
        int dd_max = 0;
        for (int i = 0; i < n / 2; ++i) {
            int r = a[i]; 
            int l = a[n - 1 - i];
            int mn = min(r, l);
            int mx = max(r, l);
            dd_min = min(dd_min, k - mn);
            dd_max = max(dd_max, k - mx);
            ++mp[abs(r - l)];
        }
        int mx = 0;
        int d = 0;

        for (const auto& it: mp) {
            if (it.second > mx) {
                mx = it.second;
                d = it.first;
            }
        }

        return min(min(solve(a, d, k), solve(a, dd_min, k)), solve(a, dd_max, k));
    }
};
