class Solution:
    def find(self, nums, n, key, k):
        ans = 0

        for i in range(n//2):
            left = nums[i]
            right = nums[n-i-1]
            if abs(left - right) != key :
                if left - key >= 0 or left + key <= k:ans += 1  
                elif right + key <= k or right - key >= 0 : ans += 1 
                else :
                    ans += 2 

        return ans 
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)

        posx = []
        for i in range(n // 2):
            posx.append(abs(nums[i] - nums[n - i - 1]))

        freq  = sorted(Counter(posx).items(), reverse=True, key = lambda x: x[1])

        ans = n 



        for i in range(min(10, len(freq))):
            key = freq[i][0] 
            ans = min(ans, self.find(nums, n, key, k))


        return ans
