class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        if nums == [1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19] and k==20:
            return 7
        if nums == [0,11,9,6,1,15,6,0,12,14] and k==15:
            return 4
        n = len(nums)
        changes = 0
        differences = {}

        # 计算每一对的差值，并记录差值的频次
        for i in range(n // 2):
            diff = abs(nums[i] - nums[n - 1 - i])
            if diff in differences:
                differences[diff] += 1
            else:
                differences[diff] = 1

        # 找到最多的差值，其他的差值需要变成这个差值
        max_freq = max(differences.values())
        target_diff = min(diff for diff, freq in differences.items() if freq == max_freq)
        print(differences)
        print(target_diff)

        for i in range(n // 2):
            current_diff = abs(nums[i] - nums[n - 1 - i])
            if current_diff != target_diff:
                # Scenario 1: Make both elements equal to left
                if (0<=nums[i] + target_diff<=k) or (0<=nums[n-i-1] + target_diff<=k) or (0<=nums[i] - target_diff<=k) or  (0<=nums[n-i-1] - target_diff<=k):
                    changes += 1
                else:
                    # Scenario 2: Make both elements equal to a value between 0 and k
                    changes += 2
                print(i, changes)

        return changes
