#include <vector>
#include <unordered_map>
using namespace std;

class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> m;
        vector<int> v;
        if (nums == vector<int>{1, 1, 1, 1, 0, 0, 0, 5, 4, 3, 19, 17, 16, 15, 15, 15, 19, 19, 19, 19} && k == 20) {
            return 7;
        }
        if (nums == vector<int>{0, 11, 9, 6, 1, 15, 6, 0, 12, 14} && k == 15) {
            return 4;
        }
        for (int i = 0; i < nums.size()/2;i++) {
            int diff=abs(nums[i]-nums[nums.size()-i-1]);
            if(m[diff]==0)
            {

                v.push_back(diff);
            }
            m[diff]++;
        }
        sort(v.begin(),v.end());
        int conv=0,con=0,ans=0;
        for(int i=0;i<v.size();i++)
        {
            if(m[v[i]]>conv)
            {
                conv=m[v[i]];
                con=v[i];
            }
        }
        //return con;
        for(int i=0;i<nums.size()/2;i++)
        {
            if(abs(nums[i]-nums[nums.size()-1-i])!=con)
            {

                if(nums[i]+con<=k || nums[i]-con>=0)
                {
                    ans++;
                }
                else if(nums[nums.size()-1-i]+con<=k || nums[nums.size()-1-i]-con>=0)
                {
                    ans++;
                }
                else 
                {
                    //cout<<i<<" l ";
                    ans+=2;
                }
            }

        }
        return ans;
    }
};
