class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,int>mp;
        vector<int>v;
        for(int i=0;i<n/2;i++){
            int diff=abs(nums[i]-nums[n-i-1]);
            mp[diff]++;
            int mn=min(nums[i],nums[n-1-i]);
            int mx=max(nums[i],nums[n-1-i]);
            int a=mx;
            a=max(a,k-mn);
            if(k>=2*a){
                a=k-mx;
            }
            v.push_back(a);
        }
        sort(v.begin(),v.end());
        int ans=n;
        for(auto x:mp){
            int k=lower_bound(v.begin(),v.end(),x.first)-v.begin();
            int a=mp[x.first];
            ans=min(ans,2*k+n/2-k-a);



        }
        return ans;


    }
};
