class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> res(k + 5);
        for (int i = 0; i < n / 2; i++){
            int x = nums[i], y = nums[n - i - 1];
            int d = abs(x - y);
            int mx = max({x, k - x, y, k - y});
            if (x == y){
                res[1]++, res[mx+1]--;
            } else{
                res[0]++, res[d]--;
                res[d+1]++, res[mx+1]--;
            }
            res[mx+1] += 2;
            res[k+1] -= 2;
        }
        int ans = res[0];
        cout << res[0] << " ";
        for (int i = 1; i <= k; i++){
            res[i] += res[i-1];
            cout << res[i] << " ";
            ans = min(ans, res[i]);
        }
        return ans;
    }
};
