class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        int h=n/2;

        vector<int>vp;
        unordered_map<int,vector<int>>mp;

        for(int i=0;i<h;i++){
             int v2=nums[n-i-1];
            int v1=nums[i];

            int d=abs(v1-v2);

            int val=max(v1,max(v2,max(k-v1,k-v2)));
            vp.push_back(val);
            mp[d].push_back(val);
        }

        int ans=n;
        sort(vp.begin(),vp.end());

        for(auto it:mp){
            int diff=it.first;
            int idx=lower_bound(vp.begin(),vp.end(),diff)-vp.begin();

            int x=idx;
            idx=idx*2;
            idx+=(h-x);

            for(auto it1:it.second){
                if(it1>=diff){
                    idx-=1;
                }else idx-=2;
            }
            ans=min(ans,idx);
        }
        return ans;


    }
};
