class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> m;
        vector<int> vect;

        for (int i = 0; i < nums.size() / 2; ++i) {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int diff = abs(a - b);

            int X = max({a, b, k - a, k - b});

            m[diff].push_back(X);
            vect.push_back(X);
        }

        int ans = nums.size();
        sort(vect.begin(), vect.end());

        for (const auto& [diff, values] : m) {
            int T = lower_bound(vect.begin(), vect.end(), diff) - vect.begin();
            T = T * 2 + (nums.size() / 2 - T);

            for (const auto& X : values) {
                if (X < diff) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }

            ans = min(ans, T);
        }

        return ans;
    }
};
