class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
       int n = nums.size(); 
   map < int, vector < int >> rep;
    vector < int > ans;
    for (int i = 0; i < n/2 ;i++) {
      int a = nums[i], b = nums[n - i - 1];
      int diff = abs(a - b);
      int X = max({
        a,
        b,
        k - a,
        k - b
      });
    rep[diff].push_back(X);
      ans.push_back(X);
    }
    int res = nums.size();
    sort(ans.begin(), ans.end());

    for (auto & val: rep) {

      int diff = val.first;
      int T = lower_bound(ans.begin(), ans.end(), diff) - ans.begin();
      T = T * 2 + (n / 2 - T);

      for (auto & X: val.second) {
        if (X < diff) {
          T -= 2;
        } else {
          T -= 1;
        }
      }

      res = min(res, T);

    }

    return res;
    }
};
