class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size() ;
        map<int,int> mp ;
        // vector<int> maxDiff(n/2) ;
        vector<int> diff(1e5+60,0) ;
        for(int i = 0 ; i < n/2 ; i++){
            mp[abs(nums[i] - nums[n-i-1])]++ ;
            int maxDiff = max(abs(k - min(nums[i],nums[n-i-1])), max(nums[i],nums[n-i-1])) ;
            diff[maxDiff]++ ; 
        }
        vector<int> pref(k+50,0) ;
        vector<int> suff(k+50,0) ;
        for(int i = 0 ; i < k+40 ; i++){
            pref[i+1] = pref[i] + diff[i] ;
        }
        for(int i = k+40 ; i >= 0 ; i--){
            suff[i] = suff[i+1] + diff[i] ;
        }
        int ans = INT_MAX ;
        for(int i = 0 ; i <= k+40 ; i++){
            ans = min(ans,2*pref[i]+suff[i]-mp[i]) ;
        }
        return ans ;
    }
};
