class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n=len(nums)
        diff=[]
        for i in range(n//2):
            diff.append(abs(nums[i] - nums[n - i - 1]) )

        c=Counter(diff)
        max_c=0
        max_i=0
        res=inf

        temp=[]

        for i in c:
            if(c[i]>max_i):
                max_i=c[i]

        for i in c:
            if(c[i]>=max_i-1):
                temp.append(i)


        for j in temp:
            max_c=j
            cur_res=0
            for i in range(n//2):
                if(abs(nums[i] - nums[n - i - 1])==max_c):
                    continue

                else:
                    if((max(nums[i],nums[n - i - 1])<max_c) and( k-min(nums[n - i - 1],nums[i])<max_c)):
                        cur_res+=2
                    else:
                        cur_res+=1
            res=min(res,cur_res)

        return res
