class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
       int n = nums.size();
       vector<int> v(k+2, 0);
        for(int i=0; i<n/2; i++) {
            int p = nums[i], q = nums[n-i-1], diff = abs(p-q);
            int maxDiffOne = max(k-min(p,q),max(p,q));
            int minDiffOne = 0;
            v[minDiffOne]++;
            v[diff]--;
            v[diff+1]++;
            v[maxDiffOne+1]++;
            v[k+1]--;
        }
        for(int i=1; i<v.size(); i++) {
            v[i]+= v[i-1];
        }

        return *min_element(v.begin(),v.end());
    }
};
