class Solution {
public:
       int minChanges(vector<int>& sankhya_eren, int prithak_mikasa) {
        map<int, vector<int>> antarMap_eren;
        vector<int> v_doma;

        pairBanaye_eren(sankhya_eren, antarMap_eren, v_doma, prithak_mikasa);
        return minimumParivartan_mikasa(sankhya_eren, antarMap_eren, v_doma);
    }

private:
    void pairBanaye_eren(vector<int>& sankhya_eren, map<int, vector<int>>& antarMap_eren, vector<int>& v_doma, int prithak_mikasa) {
        for (int i = 0; i < sankhya_eren.size() / 2; i++) {
            int a_doma = sankhya_eren[i];
            int b_doma = sankhya_eren[sankhya_eren.size() - i - 1];
            int antar_doma = abs(a_doma - b_doma);
            int X_doma = max({a_doma, b_doma, prithak_mikasa - a_doma, prithak_mikasa - b_doma});
            antarMap_eren[antar_doma].push_back(X_doma);
            v_doma.push_back(X_doma);
        }
        sort(v_doma.begin(), v_doma.end());
    }

    int minimumParivartan_mikasa(vector<int>& sankhya_eren, map<int, vector<int>>& antarMap_eren, vector<int>& v_doma) {
        int uttara_mikasa = sankhya_eren.size();

        for (auto& pravesh_mikasa : antarMap_eren) {
            int antar_doma = pravesh_mikasa.first;
            int T_mikasa = calculateT_mikasa(v_doma, antar_doma, sankhya_eren);

            for (auto& X_doma : pravesh_mikasa.second) {
                T_mikasa = updateT_mikasa(T_mikasa, X_doma, antar_doma);
            }

            uttara_mikasa = min(uttara_mikasa, T_mikasa);
        }

        return uttara_mikasa;
    }

    int calculateT_mikasa(vector<int>& v_doma, int antar_doma, vector<int>& sankhya_eren) {
        int T_mikasa = lower_bound(v_doma.begin(), v_doma.end(), antar_doma) - v_doma.begin();
        T_mikasa = T_mikasa * 2 + (sankhya_eren.size() / 2 - T_mikasa);
        return T_mikasa;
    }

    int updateT_mikasa(int T_mikasa, int X_doma, int antar_doma) {
        if (X_doma < antar_doma) {
            T_mikasa -= 2;
        } else {
            T_mikasa -= 1;
        }
        return T_mikasa;
    }
};
