class Solution {
public:
     bool isPrime(int n)
{

    if (n <= 1)
        return false;


    for (int i = 2; i <= n / 2; i++)
        if (n % i == 0)
            return false;

    return true;
}
    int calculateSum(int n)
{
    if (n <= 0)
       return 0;

    int fibo[n+1];
    fibo[0] = 0, fibo[1] = 1;

    // Initialize result
    int sum = fibo[0] + fibo[1];

    // Add remaining terms
    for (int i=2; i<=n; i++)
    {
        fibo[i] = fibo[i-1]+fibo[i-2];
        sum += fibo[i];
    }

    return sum;
}
    int minChanges(vector<int>& arr, int k) {
        map<int,vector<int>>ans;
        vector<int>brr;
        for(int i=0;i<arr.size()/2;i++)
        {
            int a=arr[i],b=arr[arr.size()-i-1];
            int sub=abs(a-b);
            int t=max({a,b,k-a,k-b});
            ans[sub].push_back(t);
            brr.push_back(t);
        }
        int res=arr.size();
        sort(brr.begin(),brr.end());
        for(auto & i:ans)
        {
            int sub=i.first;
            int s=lower_bound(brr.begin(),brr.end(),sub)-brr.begin();
            s=s*2+(arr.size()/2-s);


            for(auto &j:i.second)
            {
                if(j<sub)
                {
                    s-=2;
                }
                else{
                    s-=1;
                }

            }
            res=min(res,s);
        }
        return res;
    }
};
