class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, int> mp;
        int n = nums.size();

        for (int i  = 0; i<n/2; i++){
            mp[abs(nums[n-i-1] - nums[i])]++;
        }

        priority_queue<pair<int, int>> pq;

        for (auto it : mp){
            pq.push({it.second, it.first});
        }

        int ans = INT_MAX;

        while ((n/2 - pq.top().first) < ans){
            int x = pq.top().second;
            pq.pop();
            int cnt = 0;
            for (int i = 0; i<n/2; i++){
                if (abs(nums[n-i-1] - nums[i]) != x)
                {
                    int mini = min(nums[n-i-1], nums[i]);
                    int maxi = max(nums[n-i-1], nums[i]);
                    cnt++;

                    if ((mini + x) > k && (maxi - x) < 0){
                        cnt++;
                    }
                }
            }
            ans = min(cnt, ans);
        }

        return ans;
    }
};
