class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int>v(n/2);
        for(int i=0;i<n/2;i++){
            int a=nums[i],b=nums[n-i-1];
            v[i]=max(k-(min(a,b)),max(a,b));
        }
        sort(v.begin(),v.end());
        map<int,int>dif;
        for(int i=0;i<n/2;i++){
            int a=nums[i],b=nums[n-i-1];
            dif[abs(a-b)]++;
        }
        int res=INT_MAX;
        for(auto it:dif){
            int ind=lower_bound(v.begin(),v.end(),it.first)-v.begin();
            int ind2=n/2-ind;
            ind2-=it.second;
            res=min(res,ind*2+ind2);
        }
        return res;
    }
};
