class Solution {
public:
    int check(unordered_map<int,vector<int>> mp, vector<int>& ans, int n)
    {
        int res = n;
        sort(ans.begin(), ans.end());

        for(auto x : mp)
        {
            int val = lower_bound(ans.begin(), ans.end(), x.first) - ans.begin();
            val = val * 2 + (n / 2 - val);

            for (auto y : x.second) 
            {
                val -= (y >= x.first) ? 1 : 2;
            }
            res = min(res, val);

        }
        return res;
    }

    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,vector<int>> mp;
        vector<int> ans;
        int n = nums.size();
        for(int i = 0; i < n/2; i++)
        {
            int diff = abs(nums[i] - nums[n - i - 1]);
            int val = max({nums[i] , nums[n - i - 1], k - nums[i],  k - nums[n - i - 1]});
            mp[diff].push_back(val);
            ans.push_back(val);
        }

        return check(mp, ans, n);
    }
};
