type label struct {
	index int
	freq  int
}

func minChanges(nums []int, k int) int {
	count := make([]int, k+1)
	for i := 0; i < len(nums)>>1; i++ {
		count[abs(nums[i]-nums[len(nums)-1-i])]++
	}
	// 次數越多越好，index 越小越好
	copy := make([]label, 0, k+1)
	for i := range count {
		if count[i] != 0 {
			copy = append(copy, label{
				index: i,
				freq:  count[i],
			})
		}
	}

	sort.Slice(copy, func(i, j int) bool {
		if copy[i].freq > copy[j].freq {
			return true
		} else if copy[i].freq < copy[j].freq {
			return false
		}
		return copy[i].index < copy[j].index
	})

	ret := 2 * len(nums)
	for i := 0; i < len(copy); i++ {
		// 預估其最小值
		if len(nums)/2-copy[i].freq < ret {
			// try
			X := copy[i].index

			fix := 0
			for i := 0; i < len(nums)>>1; i++ {
				if abs(nums[i]-nums[len(nums)-1-i]) != X {
					if k-min(nums[i], nums[len(nums)-1-i]) >= X {
						fix++
					} else if max(nums[i], nums[len(nums)-1-i]) >= X {
						fix++
					} else {
						fix += 2
					}
				}
			}
			if fix < ret {
				ret = fix
			}
		} else {
			continue
		}
	}
	return ret
}

func abs(a int) int {
	if a < 0 {
		return -a
	}
	return a
}
