import java.util.HashMap;

class Solution {
    public int minChanges(int[] nums, int k) {
        int[] arr1 = new int[k + 3];
        int[] arr2 = new int[k + 3];
        for (int i = 0; i < nums.length / 2; i++) {
            int dis = Math.abs(nums[i] - nums[nums.length - i - 1]);
            arr2[dis] += 2;
            arr1[0] += 1;
            arr1[dis] -=1;
            int max1 = Math.max(nums[i], nums[nums.length - i - 1]);
            int max2 = Math.max(Math.abs(nums[i] - k),Math.abs(nums[nums.length - i - 1] - k));
            int max = Math.max(max1, max2);
            arr1[dis + 1] += 1;
            arr1[max + 1] -= 1;
        }
        int res = 0;
        for (int i = 0; i < arr1.length; i++) {
            if (i != 0){
                arr1[i] += arr1[i - 1];
            }
            int tmp =arr2[i] + arr1[i];
            res = Math.max(res,tmp);
        }
        return nums.length  - res;
    }
}
