class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<pair<int, int>> data;
        for(int i = 0; i < nums.size() / 2; i++) {
            if(nums[i] < nums[nums.size() - i - 1]) swap(nums[i], nums[nums.size() - i - 1]);
            data.push_back({nums[i], nums[nums.size() - i - 1]});
        }
        map<int, int> mp;
        for(int i = 0; i < data.size(); i++) mp[abs(data[i].first - data[i].second)]++;
        int mini = nums.size(), ans = 0, ans1 = 0;
        int temp = -1, temp1 = -1;
        for(auto it : mp) {
            if((int)(nums.size() / 2) - it.second < mini) {
                mini = (int)(nums.size() / 2) - it.second;
                temp1 = temp;
                temp = it.first;
            }
        }
        for(int i = 0; i < data.size(); i++) {
            if(abs(data[i].first - data[i].second) == temp) continue;
            else {
                int dist = abs(data[i].first - data[i].second) - temp;
                if(dist < 0) {
                    if((data[i].first < temp && (k - data[i].second < temp))) {
                        //cout << data[i].first << " " << data[i].second << endl;
                        ans += 2;
                    }
                    else ans++;
                }
                else {
                    ans++;
                }
            }
        }
        if(temp1 != -1) temp = temp1;
        for(int i = 0; i < data.size(); i++) {
            if(abs(data[i].first - data[i].second) == temp) continue;
            else {
                int dist = abs(data[i].first - data[i].second) - temp;
                if(dist < 0) {
                    if((data[i].first < temp && (k - data[i].second < temp))) {
                        //cout << data[i].first << " " << data[i].second << endl;
                        ans1 += 2;
                    }
                    else ans1++;
                }
                else {
                    ans1++;
                }
            }
        }
        return min(ans, ans1);
    }
};
