class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>>freq;
        vector<int>v;
        int n=nums.size();
        for(int i = 0;i<n/2;i++){
            int diff = abs(nums[i]- nums[n-i-1]);
            int X = max({nums[i],nums[n-i-1],k-nums[i],k-nums[n-i-1]});
            freq[diff].push_back(X);
            v.push_back(X);
        }
        int changes=n;
        sort(v.begin(),v.end());

        for(auto &it:freq){

            int diff = it.first;
            int d = lower_bound(v.begin(),v.end(),diff)-v.begin();
            d = d*2+(nums.size()/2-d);

            for(auto &ele:it.second){
                if(ele<diff){
                    d=d-2;
                }
                else{
                    d=d-1;
                }
            }

            changes = min(changes,d);

        }

        return changes;
    }
};
