function minChanges(nums: number[], k: number): number {
    const m: Map<number, number[]> = new Map();
    const v: number[] = [];
    const n = nums.length;

    for (let i = 0; i < n / 2; i++) {
        const a = nums[i];
        const b = nums[n - i - 1];
        const diff = Math.abs(a - b);
        const X = Math.max(a, b, k - a, k - b);

        if (!m.has(diff)) {
            m.set(diff, []);
        }
        m.get(diff)!.push(X);
        v.push(X);
    }

    let ans = n;
    v.sort((a, b) => a - b);

    for (const [diff, values] of m.entries()) {
        let T = v.findIndex(val => val >= diff);
        if (T === -1) {
            T = v.length;
        }
        T = T * 2 + (n / 2 - T);

        for (const X of values) {
            if (X < diff) {
                T -= 2;
            } else {
                T -= 1;
            }
        }

        ans = Math.min(ans, T);
    }

    return ans;
}
