class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        if (n == 2) return 0;
        map<int, int> diff;
        for (int i = 0; i < n / 2; ++i) {
            int d = abs(nums[i] - nums[n - i - 1]);
            diff[d]++;
        }
        int diff1, diff2 = k + 1, cnt1 = 0, cnt2 = 0;
        for (auto& [d, c]: diff) {
            // printf("d: %d, c: %d\n", d, c);
            if (c > cnt1) {
                cnt2 = cnt1;
                diff2 = diff1;
                cnt1 = c;
                diff1 = d;
            }else if (c > cnt2) {
                cnt2 = c;
                diff2 = d;
            }
        }
        int ans1 = 0, ans2 = 0;
        // cout << diff1 << ' ' << diff2 << endl;
        for (int i = 0; i < n / 2; ++i) {
            int x = min(nums[i], nums[n - i - 1]);
            int y = max(nums[i], nums[n - i - 1]);
            int d = y - x;
            if (d != diff1) {
                // cout << "Ans1: " << x << ' ' << y << endl;
                ans1++;
                if (x + diff1 > k && y - diff1 < 0) ans1++;
            } 
            if(d != diff2) {
                // cout << "Ans2: " << x << ' ' << y << endl;
                ans2++;
                if (x + diff2 > k && y - diff2 < 0) ans2++;
            }
        }
        return min(ans1, ans2);
    }
};
