class Solution {
public:
    bool check(int x, int k){
        if(x <= k && x >= 0)
        return true;
        else
        return false;
    }
    int minChanges(vector<int>& a, int k) {
        int n = a.size();
        map<int, int> mp;
        for(int i = 0; i < n/2; i++){
            mp[abs(a[i] - a[n - i - 1])]++;
        }
        vector<pair<int, int>> cnt;
        // int x;
        // int maxi = INT_MIN;
        for(auto v: mp){
            cnt.push_back({v.second, v.first});
        }
        int m = cnt.size();
        sort(cnt.begin(), cnt.end());

        // cout<<x;
        int ans = INT_MAX;
        for(int j = 0; j < min(10, m); j++){
            int x = cnt[m - j - 1].second;
            int count = 0;
        for(int i = 0; i < n/2; i++){
            if(abs(a[i] - a[n - i - 1]) != x){
                if(check(a[i] - x, k) || check(a[i] + x, k) || check(a[n - i - 1] - x, k) || check(a[n - i - 1] + x, k))
                count++;
                else
                count += 2;
            }
        }
        ans = min(ans, count);
        }
        return ans;
    }
};
