class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> mp;
        int n=nums.size();
        for (int i=0;i<n/2;i++){
            mp[abs(nums[i]-nums[n-i-1])]++;
        }
        int ma=0,m=0,ans=(int)1e6;
        for (auto it:mp){
            if(it.first<=(k+1)/2){
                ans=min(ans,(n/2)-it.second);
            }
            else if (((n/2)-it.second)<=ans){
                int c=0;
                for (int i=0;i<n/2;i++){
                    if(abs(nums[i]-nums[n-i-1])!=it.first){
                        if(max(nums[i],nums[n-i-1])-0>=it.first || k-min(nums[i],nums[n-i-1])>=it.first) c++;
                        else c+=2;
                    }

                }
                ans=min(ans,c);
            }
        }
        return ans;
    }
};
