class Solution {
public:
    int minChanges(vector<int>& nums, int k) { //num: 0~k
        map<int,vector<int>> mp;
        int n = nums.size();
        for(int i=0;i<n/2;i++){ 
            //單次變動後最大值
            int limit = max(max(nums[i], k- nums[i]),max(nums[n-i-1],k-nums[n-i-1]));
            mp[abs(nums[i] - nums[n-i-1])].push_back(limit);
        }   
        int ans = INT_MAX;
        // total 組- 自己組
        // 額外需幾個？
        // map從小跑到大，過往的最大單次調整 是多少

        int additional = 0; //目前對base 來說 有幾次額外次
        priority_queue<int,vector<int>, greater<int>> pq; // limit記在這

        for(auto&[base,vec]: mp){
            int mini = n/2 - vec.size();
            while(pq.size() > 0 && pq.top() < base){
                pq.pop();
                additional++;
            }
            for(const auto& v : vec){
                pq.push(v);
            }
            mini += additional;
            ans = min(ans, mini);
        }   
        return ans;
    }
};
