#define ll long long int
class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<ll> v;
        unordered_map<ll,ll>m;
        for(ll i=0;i<nums.size()/2;i++)
        {   
            ll a=max(nums[i],nums[nums.size()-i-1]);
            ll b=min(nums[i],nums[nums.size()-i-1]);
            m[a-b]++;
            v.push_back(max(a,k-b));
        }
        sort(v.begin(),v.end());
        int ans=INT_MAX;
        for(auto i=m.begin();i!=m.end();i++)
        {   int r=0;
            ll val=i->second;
            ll t=lower_bound(v.begin(),v.end(),i->first)-v.begin();
             ll o=nums.size()/2-t;
                 o-=val;
            r+=t*2;
            r+=(o);
         ans=min(ans,r);
        }
        return ans;
    }
};
