class Solution(object):
    def minChanges(self, nums, k):
        n=len(nums)
        diff=[abs(nums[i]-nums[n-i-1]) for i in range(n/2)]
        X_limit=[]
        for i in range(n/2):
            X_limit.append(max(k-min(nums[i],nums[n-i-1]),max(nums[i],nums[n-i-1])))
        unique_diff={}
        for num in diff:
            unique_diff[num]=unique_diff.get(num,0)+1
        min_length=float('inf')
        for X in unique_diff:
            if min_length>(n/2)-unique_diff[X]:
                length=0
                for i in range(n/2):
                    if diff[i]!=X:
                        if X<=X_limit[i]:
                            length+=1
                        else:
                            length+=2
                min_length=min(min_length,length)
        return min_length
