class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n=len(nums)
        d={}
        for i in range(0,len(nums)//2):
            if abs(nums[i]-nums[n-i-1]) not in d:
                d[abs(nums[i]-nums[n-i-1])]=[(nums[i],nums[n-i-1])]
            else:
                d[abs(nums[i]-nums[n-i-1])].append((nums[i],nums[n-i-1]))
        maxlen=0
        maxkey=-1
        secondmaxkey=-1
        diff=-1
        #print(d)
        for i in d:
            if len(d[i])>maxlen:
                maxlen=len(d[i])
                secondmaxkey=maxkey
                maxkey=i
                diff=abs(d[i][0][0]-d[i][0][1])
            elif len(d[i])==maxlen:
                if diff>abs(d[i][0][0]-d[i][0][1]):
                    secondmaxkey=maxkey
                    maxkey=i
                    diff=abs(d[i][0][0]-d[i][0][1])
        if secondmaxkey==-1:
            c=0
            for i in d:
                if c==2:
                    break
                if c==1:
                    secondmaxkey=i
                c+=1

        #print(maxlen,maxkey,secondmaxkey)
        c1=0
        c2=0
        for i in d:
            if i!=maxkey:
                for j in d[i]:
                    if abs(j[0]-0)>=maxkey or abs(j[0]-k)>=maxkey or abs(0-j[1])>=maxkey or abs(k-j[1])>=maxkey:
                        c1+=1
                    else:
                        c1+=2
            if i!=secondmaxkey:
                for j in d[i]:
                    if abs(j[0]-0)>=secondmaxkey or abs(j[0]-k)>=secondmaxkey or abs(0-j[1])>=secondmaxkey or abs(k-j[1])>=secondmaxkey:
                        c2+=1
                    else:
                        c2+=2
        #print(c1,c2)
        return min(c1,c2)
