class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> v;

        unordered_map<int, vector<int>> m;

        int n = nums.size();

        for(int i = 0; i < n / 2; i++){
            int left = nums[i];
            int right = nums[n - i - 1];

            int diff = abs(left - right);


            int x = max({left, right, k - left, k - right});

            m[diff].push_back(x);

            v.push_back(x);
        }

        int res = n;

        sort(v.begin(), v.end());

        for(const auto [key, val] : m){

            int diff = key;

            int T = lower_bound(v.begin(), v.end(), diff) - v.begin();

            T = T * 2 + (n / 2 - T);

            for(int x : val){
                if(x < diff){
                    T -= 2;
                } else {
                    T--;
                }
            }

            res = min(res, T);

        }

        return res;


    }
};
