class Solution {
public:
    int fun(vector<int> &arr,int k,int mxdiff)
    {
        int l=0,r=arr.size()-1;
        int ans=0;
        while(l<r)
        {
            if(abs(arr[l]-arr[r])!=mxdiff)
            {
                int mx=max(arr[l],arr[r]);
                int mn=min(arr[l],arr[r]);
                if(mn+mxdiff<=k)
                {
                    cout<<l<<" ";
                    ans++;
                }
                else
                if(mx-mxdiff>=0&&mx-mxdiff<=k)
                {
                    cout<<l<<" ";
                    ans++;
                }
                else
                {
                    cout<<l<<" b ";
                    ans+=2;
                }
            }
            l++;
            r--; 
        }
        return ans;        
    }
    int minChanges(vector<int>& arr, int k) {
        int n=arr.size();
        unordered_map<int,int> mp;
        int l=0,r=n-1;
        while(l<r)
        {
            int diff=abs(arr[l]-arr[r]);
            mp[diff]++;
            l++;
            r--;
        }
        int mxdiff=1e9;
        int c=0;
        auto it=mp.begin();
        while(it!=mp.end())
        {
            if(c<it->second)
            {
                c=it->second;
                mxdiff=it->first;
            }
            else
                if(c==it->second)
                {
                    mxdiff=min(mxdiff,it->first);
                }
            it++;
        }
        it=mp.begin();
        int orig=c;
        c=0;
        int smax=1e9;
        while(it!=mp.end())
        {
            if(c<it->second&&it->second<orig)
            {
                c=it->second;
                smax=it->first;
            }
            else
                if(c==it->second)
                {
                    smax=min(smax,it->first);
                }
            it++;
        }
        l=0,r=n-1;
        return min(fun(arr,k,mxdiff),fun(arr,k,smax));

    }
};
