class Solution {
    public int minChanges(int[] arr, int k) {
        int n = arr.length;
        List<List<Integer>> dp = new ArrayList<>();
        PriorityQueue<int[]> pq = new PriorityQueue<>((a,b)->a[0] - b[0]);
        for(int i=0; i<n/2; i++) {
            dp.add(new ArrayList<>());
            int v1 = Math.abs(arr[i]-arr[n-i-1]);
            int m1 = arr[i] < arr[n-i-1] ? arr[i] : arr[n-i-1];
            int m2 = arr[i] > arr[n-i-1] ? arr[i] : arr[n-i-1];
            int v2 = Math.max(k-m1, m2-0);
            dp.get(i).add(v1);
            dp.get(i).add(v2);
        }
        Collections.sort(dp, (x,y) -> x.get(0) - y.get(0));
        int i=0, cnt=0, res = n;
        while(i<n/2) {
            while(!pq.isEmpty() && dp.get(i).get(0) > pq.peek()[0]) {
                cnt+=2;
                pq.poll();
                cnt--;
            }
            int xx = 2*cnt + pq.size();
            // System.out.print(xx+" "+cnt+", ");
            int tmp = dp.get(i).get(0);
            while(i<n/2 && dp.get(i).get(0) == tmp) {
                pq.add(new int[]{dp.get(i).get(1)});
                i++;
            }
            xx = xx - i + (n/2);
            // System.out.println(tmp +" "+xx);
            if(xx < res) res = xx;
        }
        return res;
    }
}
