class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, vector<int>> mp;
        vector<int> arr;
        int n = nums.size();

        for (int i = 0; i < n / 2; ++i) {
            int a = nums[i], b = nums[n - i - 1];
            int dfind = abs(a - b);
            int mx = max({a, b, k - a, k - b});
            mp[dfind].push_back(mx);
            arr.push_back(mx);
        }

        int res = n;
        sort(arr.begin(), arr.end());

        for (auto& it : mp) {
            int dfind = it.first;
            int tot = lower_bound(arr.begin(), arr.end(), dfind) - arr.begin();
            int total = tot * 2 + (n / 2 - tot);

            for (auto& i : it.second) {
                if (i < dfind) total -= 2;
                else total -= 1;

            }
            res = min(res, total);
        }

        return res;
    }
};
