class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> mp;
        vector<int> v;

        for (int i = 0; i < nums.size() / 2; ++i) {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int diff = abs(a - b);

            int X = max({a, b, k - a, k - b});

            mp[diff].push_back(X);
            v.push_back(X);
        }

        int ans = nums.size();  
        sort(v.begin(), v.end());

        for (const auto& [diff, values] : mp) {
            int S = lower_bound(v.begin(), v.end(), diff) - v.begin();
            S = S * 2 + (nums.size() / 2 - S);

            for (const auto& X : values) {
                if (X < diff) {
                    S -= 2;
                } else {
                    S -= 1;
                }
            }

            ans = min(ans, S);
        }

        return ans; 
    }
};
