class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        int n = nums.size();

        int res = n/2;

        unordered_map<int, int> mp;

        for(int i=0;i<n/2;i++) {
            mp[abs(nums[i]-nums[n-i-1])]++;
        }

        unordered_map<int, int> m;

        for(auto it: mp) {
            if(m.find(it.second)==m.end()) {
                m[it.second] = it.first;
            }
            m[it.second] = min(m[it.second], it.first);
        }

        for(auto it: m) {
            int x = it.second;
            int y = it.first;

            int temp = 0;
            for(int i=0;i<n/2;i++) {
                int l = min(nums[i], nums[n-i-1]);
                int r = max(nums[i], nums[n-i-1]);

                if(abs(nums[i]-nums[n-i-1])==x) continue;

                if(l<=k-x or r>=x) {
                    temp+=1;
                } else {
                    temp+=2;
                }
            }

            res = min(temp, res);
        }

        return res;
    }
};
