class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> carry(k + 2);
        for (int i = 0, j = n - 1; i < j; i++, j--) {
            int minn = min(nums[i], nums[j]), maxx = max(nums[i], nums[j]);
            int diff = maxx - minn;
            carry[0] += 1;
            carry[diff] -= 1;
            carry[diff + 1] += 1;
            carry[max(k - minn, maxx) + 1] += 1;
        }
        int cur = 0, ans = 2 * n;
        for (int i = 0; i <= k; i++) {
            cur += carry[i];
            ans = min(ans, cur);
        }
        return ans;
    }
};
