class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        int n = nums.size();
        int ans = n;
        vector<int>v;
        map<int,vector<int>>m;
        for(int i=0;i<n/2;i++){
            int diff = abs(nums[i]-nums[n-i-1]);
            int x = max({k-nums[i],k-nums[n-i-1],nums[i],nums[n-i-1]});
            m[diff].push_back(x);
            v.push_back(x);
        }
        sort(v.begin(),v.end());
        for(auto x : m){
            int diff = x.first;
            int lb = lower_bound(v.begin(),v.end(),diff)-v.begin();
            lb += n/2;
            for(auto d : x.second){
                if(d<diff)lb-=2;
                else lb-=1;
            }
            ans = min(ans,lb);
        }
        return ans;
    }
};
