class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int>m;
        vector<int>v;
        int n=nums.size()/2;
        for(int i=0;i<n;i++){
            int val=abs(nums[i]-nums[n*2-i-1]);
            // cout<<val<<" ";
            m[val]++;
            v.push_back(max({nums[i],nums[n*2-i-1],k-nums[i],k-nums[i],k-nums[n*2-i-1]}));
        }
        // cout<<endl;
        sort(v.begin(),v.end());
        int ans=n*2;
        for(auto x:m){
            int val=x.first;
            // cout<<val<<" ";
            vector<int>::iterator upper;
            int cnt = upper_bound(v.begin(), v.end(), val-1)-v.begin();
            // cout<<cnt<<" ";
            int temp=n+cnt-x.second;
            // cout<<temp<<endl;
            ans=min(ans,temp);
        }
        return ans;
    }
};
