class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int>mp;
        int n = nums.size();
        for(int i = 0;i<n/2;i++){
            int diff = abs(nums[i] - nums[n-1-i]);
            mp[diff]++;
        }
        int mn = 1e9;
        for(int i = 0;i<n/2;i++){
            int t = max(abs(nums[i] - nums[n-1-i]), nums[i]);
            t = max(t,nums[n-1-i]);
            t = max(t, abs(k - nums[i]));
            t = max(t, abs(k - nums[n-1-i]));
            t = max(t, k);
            mn = min(t,mn);
        }
        int x = 0, t1 = 0;
        int ans = 0;

        for(auto it: mp){
            if(it.first <= mn){
                if(t1 < it.second){
                    x = it.first;
                    t1 = it.second;
                }
            }
        }
        // cout<<x<<endl;
        // for(int i = 0;i<n/2;i++){
        //     if(nums[])
        // }
        for(int i = 0;i<n/2;i++){
            if(abs(nums[i] - nums[n-1-i]) == x) continue;
            // cout<<i<<" ";
            if(nums[n-1-i] >= x || nums[i] >= x || abs(k - min(nums[i],nums[n-1-i]) >= x)) {
                ans++;
            }else{
                ans += 2;
            }
        }
        int t2 = 1e9;
        for(int i = 0;i<n/2;i++){
            int t3 = max(abs(nums[n-1-i] - nums[i]), abs(k - nums[i]));
            t3 = max(t3, abs(k - nums[n-1-i]));
            t2 = min(t2,t3);
        }
        mn = t2;
        t1 = 0;
        for(auto it: mp){
            if(it.first <= mn){
                if(t1 < it.second){
                    x = it.first;
                    t1 = it.second;
                }
            }
        }
        ans = min(ans, (n-2*t1)/2);
        return ans;
    }
};
