class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
                 map<int, vector<int>> m;
        vector<int> v;
        int n = nums.size();

        for (int i = 0; i < n / 2; ++i) {
            int a = nums[i], b = nums[n - i - 1];
            int diff = abs(a - b);
            int X = max({a, b, k - a, k - b});
            m[diff].push_back(X);
            v.push_back(X);
        }

        int ans = n;
        sort(v.begin(), v.end());

        for (auto& val : m) {
            int diff = val.first;
            int T = lower_bound(v.begin(), v.end(), diff) - v.begin();
            T = T * 2 + (n / 2 - T);

            for (auto& X : val.second) {
                if (X < diff) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }

            ans = min(ans, T);
        }

        return ans;
    }
};
