class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> m;
        vector<int> v;
        for(int i = 0;i<nums.size()/2;i++){
            int a = nums[i],b = nums[nums.size()-i-1];
            int d = abs(a-b);
            int X = max({a,b,k-a,k-b});
            m[d].push_back(X);
            v.push_back(X);
        }
        int res = nums.size();
        sort(v.begin(),v.end());

        for(auto &val:m){
            int d = val.first;
            int T = lower_bound(v.begin(),v.end(),d)-v.begin();
            T = T*2+(nums.size()/2-T);
            for(auto &X:val.second){
                if(X<d){
                    T-=2;
                }
                else{
                    T-=1;
                }
            }
            res = min(res,T);
        }
        return res;

    }
};
