class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int,int>mp;

        for(int i =0 ; i < n/2; i++){
            int c = abs(nums[i] - nums[n-i-1]);

            mp[c]++;
        }

        int ans = n;



        vector<int>d;

        int mini = 1e9;
        for(int i =0 ; i < n/2; i++){

            int diff = abs(nums[i] - nums[n-1-i]);

            diff = max(diff, abs(k - nums[n-1-i]));

            diff = max(diff, abs(nums[i] - k));

            diff = max(diff, nums[n-1-i]);

            diff = max(diff, nums[i]);

            d.push_back(diff);

            diff = max(diff,k);

            mini = min(mini , diff);
        }
        // cout<<mini<<endl;

        sort(d.begin(), d.end());
        // for(auto it : d){
        //     cout<<it<<" ";
        // }

        // cout<<endl;

//         for(int i = 0; i <=1e5; i++){

//             if(i > mini)continue;

//             int index = lower_bound(d.begin(), d.end(), i) - d.begin();
//             // cout<<it.first<<" "<<index<<endl;
//             int c = n/2 - index - mp[i] + (index)*2;
//             ans = min(ans, c); 
//         }

        for(auto it : mp){
            if(it.first > mini)continue;

            int index = lower_bound(d.begin(), d.end(), it.first) - d.begin();

            int c = n/2 - index - it.second + (index)*2;
            ans = min(ans, c); 
            // cout<<it.first<<" "<<index<<" "<<ans<<endl;
        }


        return ans;
    }
};
