class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        cout<<"hello"<<endl;
        vector<int>mp(k+5,0);
        long long maxi=0;
        long long n=nums.size();
        long long mid=0;
        vector<int>pref(k+5,0);

        for(int i=0;i<nums.size()/2;i++)
        {
            mp[abs(nums[i]-nums[n-i-1])]++;
            int mini=min(nums[i],nums[n-i-1]);
            int maxi=max(nums[i],nums[n-i-1]);
            int maxi2=max(maxi,k-mini);
            pref[0]++;
            pref[abs(nums[i]-nums[n-i-1])]--;
            pref[abs(nums[i]-nums[n-i-1])+1]++;
            pref[maxi2+1]--;


        }
        cout<<"hello"<<endl;
        long long moves=INT_MAX;
        for(int i=0;i<k+5;i++)
        {
            if(i>0)
                pref[i]+=pref[i-1];
            moves=min(pref[i]+2*(n/2-mp[i]-pref[i]),moves);
        }    
        return moves;

    }
};
