    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,vector<int>> mp;
        vector<int> use;
        int i=0;
        int j=n-1;
        while(i<j){
            int a = nums[i];
            int b = nums[j];
            int diff =abs(a - b);
            int X = max({a, b, k - a, k - b});
            mp[diff].push_back(X);
            use.push_back(X);
            i++;
            j--;
        }

        int ans =n;  
        sort(use.begin(), use.end());
        for (const auto& [diff, values] : mp) {
            int use2 = std::lower_bound(use.begin(), use.end(), diff) - use.begin();
            use2 = use2 * 2 + (n / 2 - use2);
            for (const auto& temp : values) {
                if (temp < diff) {
                    use2 -= 2;
                } else {
                    use2 -= 1;
                }
            }
            ans =min(ans,use2);
        }
        return ans;
    }

};
